INSERT INTO `news` (`id`, `title`, `content`) VALUES
	(1, 'DALAM DERASNYA HUJAN \nMEREKA TETAP BERTAHAN ', 'Gaza menghadapi musim dingin yang mematikan tahun ini.<br>\r\nBukan hanya bertahan dalam bengisnya peperangan tapi akibat musim dingin yang sudah tiba di hadapan.<br>\r\n<br>\r\nAngin malam yang sangat dingin merasuk ke dalam tulang-tulang, membuat tubuh mati rasa tanpa diimbangi dengan makanan dan penghangat ruangan.<br>\r\n<br>\r\nLuar biasa penderitaan Gaza. Tak ada kata-kata yang dapat mengungkapkannya lagi.<br>\r\n<br>\r\nAyo kita kuatkan saudara kita di Palestina di Palestina dengan doa dan donasi terbaik kita melalui <br>\r\n<br>\r\nRekening donasi <br>\r\n8306 1101 0104 0001<br>\r\nan. Sekolah Impian Peduli&#160;Palestina'),
	(2, 'Sebuah Video Animasi Beredar', 'Organisasi permukiman ilegal Temple Mount Activists menerbitkan klip video imajiner menggunakan perangkat lunak montase yang menunjukkan pembakaran Masjid Al-Aqsha, dengan komentar: &quot;Segera dalam hari-hari ini.&quot; Kamis (12/9)<br>\r\n<br>\r\nTidak henti-hentinya Israel membuat makar terhadap Masjid Al Aqsha. Meskipun hanya berupa video imajiner, tapi membuat kita khawatir akan tercerabutnya kemuliaan Masjid Al Aqsha.<br>\r\n<br>\r\nAyo kita terus bentengi tempat mulia itu dengan sekuat tenaga.<br>\r\n<br>\r\nKirimkan Doa dan donasi terbaik Anda. <br>\r\n<br>\r\nRekening donasi <br>\r\n8306 1101 0104 0001<br>\r\nan. Sekolah Impian Peduli&#160;Palestina'),
	(3, 'Ambulance KNRP masih setia membersamai rakyat Palestina ', 'Di tengah-tengah perang yang berkecamuk dan minimnya bantuan kemanusiaan yang di dapat rakyat Palestina khususnya di Gaza, maka Alhamdulillah ambulance KNRP masih tetap berfungsi membantu proses evakuasi di sana.<br>\r\n<br>\r\nBantuan ini terbilang cukup berharga diantara bantuan lainnya yang turut menyertai perjuangan rakyat Palestina.<br>\r\n<br>\r\nAyo tetap berikan kontribusi yang terbaik melalui do\'a dan donasi.<br>\r\n<br>\r\nRekening donasi <br>\r\n8306 1101 0104 0001<br>\r\nan. Sekolah Impian Peduli&#160;Palestina'),
	(4, 'Paket Sembako untuk Palestina', 'Saudara-saudara di Gaza setiap harinya berjuang untuk bertahan hidup. 11 bulan blokade akibat agresi israel, warga Gaza semakin terlilit rasa lapar.<br>\r\n<br>\r\nYuk, bantu redakan rasa lapar mereka, hanya dengan Rp 450.000, sahabat telah memudahkan hadirnya 1 paket sembako yang sangat dibutuhkan oleh keluarga di Palestina.<br>\r\n<br>\r\nSetiap bantuanmu ada nyawa yang terselamatkan. Mari berikan harapan bagi mereka &#10071;<br>\r\n<br>\r\nRekening donasi <br>\r\n8306 1101 0104 0001<br>\r\nan. Sekolah Impian Peduli&#160;Palestina'),
	(5, 'Resolusi PBB mengatakan Israel harus hengkang dari Palestina', 'Resolusi ini bersifat mendukung ICJ (Mahkamah Internasional) dan UNSC (Dewan Keamanan PBB).<br>'),
	(6, 'Musim Dingin di Gaza', 'Saudara-saudara di Gaza setiap harinya berjuang untuk bertahan hidup. 11 bulan blokade akibat agresi israel, warga Gaza semakin terlilit rasa lapar.<br>\r\n<br>\r\nYuk, bantu redakan rasa lapar mereka, hanya dengan Rp 450.000, sahabat telah memudahkan hadirnya 1 paket sembako yang sangat dibutuhkan oleh keluarga di Palestina.<br>\r\n<br>\r\nSetiap bantuanmu ada nyawa yang terselamatkan. Mari berikan harapan bagi mereka &#10071;<br>\r\n<br>\r\nRekening donasi <br>\r\n8306 1101 0104 0001<br>\r\nan. Sekolah Impian Peduli&#160;Palestina'),
	(7, 'PENYALURAN ROTI UNTUK JALUR GAZA', 'Atas segala kemudahan dari Allah, penyaluran bantuan roti hangat berhasil tersampaikan kepada 2.240 penerima manfaat di Al-Mawashi, Jalur Gaza bagian Selatan, pada Ahad (22/9/2024). <br>\r\n<br>\r\nSenyuman anak-anak Palestina kembali merekah, menerima kiriman roti dari para donatur Indonesia. <br>\r\n<br>\r\nSemoga bantuan ini menjadi amal jariyah bagi para donatur serta seluruh pihak yang telah berpartisipasi. &#55358;&#56626;&#55356;&#57339;<br>\r\n<br>\r\n&#10024; Mari terus doakan dan bantu saudara-saudara Palestina yang semakin tertindas. &#10024;<br>\r\n<br>\r\nRekening donasi <br>\r\n8306 1101 0104 0001<br>\r\nan. Sekolah Impian Peduli&#160;Palestina');
